-- ----------------------------
-- Procedure structure for sp_update_bbs_articles_Freshness
-- ----------------------------
DROP PROCEDURE IF EXISTS `sp_update_bbs_articles_Freshness`;
delimiter ;;
CREATE PROCEDURE `sp_update_bbs_articles_Freshness`()
  COMMENT '每天刷新文章的新鲜度'
BEGIN
    -- 当前时间戳
    DECLARE currentTimestamp INT DEFAULT UNIX_TIMESTAMP(NOW());

    -- 使用公式更新 Freshness
    UPDATE bbs_articles
    SET Freshness = 1 / (1 + 0.15 * FLOOR((currentTimestamp - PublishDateTime) / 86400));
END
;;
delimiter ;

